	memcpy(panel->aid_set, S6E3HA2_SEQ_AOR_CONTROL, S6E3HA2_AID_CMD_CNT);
	panel->aid_len = S6E3HA2_AID_LEN + 1;
	panel->aid_reg_offset = S6E3HA2_AID_LEN - 1;

	memcpy(panel->vint_set, S6E3HA2_SEQ_VINT_SET, sizeof(S6E3HA2_SEQ_VINT_SET));
