#ifdef CONFIG_ALWAYS_RELOAD_MTP_FACTORY_BUILD
static int lcd_reload_mtp(int lcd_type, struct dsim_device *dsim)
{
	int i, ret;
	unsigned char mtp[S6E3HA2_MTP_SIZE] = { 0, };	// all value is 35
	unsigned char hbm[S6E3HF4_HBMGAMMA_LEN] = { 0, };	// maximum value is S6E3HF4_HBMGAMMA_LEN

	// retry 3 times
	for( i=0; i <3; i++ ) {
		switch (lcd_type) {
			case LCD_TYPE_S6E3HA2_WQHD:
				ret = s6e3ha2_read_init_info(dsim, mtp, hbm);
				dsim_info( "%s : load MTP of s6e3ha2\n", __func__ );
				break;
			case LCD_TYPE_S6E3HA3_WQHD:
				ret = s6e3ha3_read_init_info(dsim, mtp, hbm);
				dsim_info( "%s : load MTP of s6e3ha3\n", __func__ );
				break;
			case LCD_TYPE_S6E3HF4_WQHD:
				ret = s6e3hf4_read_init_info(dsim, mtp, hbm);
				s6e3hf4_init_default_info(dsim);
				dsim_info( "%s : load MTP of s6e3hf4\n", __func__ );
				break;
			default:
				ret = s6e3ha3_read_init_info(dsim, mtp, hbm);
				dsim_info( "%s : load MTP of s6e3ha3(default)\n", __func__ );
				break;
		}
		if( ret == 0 ) break;
	}
	if( ret != 0 ) return -EIO;

	update_mdnie_coordinate( dsim->priv.coordinate[0], dsim->priv.coordinate[1] );

#ifdef CONFIG_PANEL_AID_DIMMING
	ret = init_dimming(dsim, mtp, hbm);
	if (ret) {
		dsim_err("%s : failed to generate gamma tablen\n", __func__);
	}
#endif

	return 0;	//success
}
#endif	// CONFIG_ALWAYS_RELOAD_MTP_FACTORY_BUILD


struct dsim_panel_ops *dsim_panel_get_priv_ops(struct dsim_device *dsim)
{
	struct device *dev = dsim->dev;

	dsim->priv.disp_type_gpio = of_get_named_gpio(dev->of_node, "disp_type_gpio", 0);

	if(!dsim->priv.disp_type_gpio) {
		dsim_info("failed to get gpio number for disp_type\n");
		dynamic_lcd_type = LCD_TYPE_S6E3HF4_WQHD;
		goto exit_panel_ops;
	}

	dsim->priv.panel_type = gpio_get_value(dsim->priv.disp_type_gpio);
	dsim_info("%s : gpio %d , value %d \n",
		__func__, dsim->priv.disp_type_gpio, dsim->priv.panel_type);

	if(dsim->priv.panel_type) { // ha2/ha3
		if(lcdtype & LCD_TYPE_MASK) {
			dynamic_lcd_type = LCD_TYPE_S6E3HA3_WQHD;
			dsim_info("LCD TYPE : S6E3HA3 (WQHD) : %d type %x\n", dynamic_lcd_type, lcdtype);
		} else {
			dynamic_lcd_type = LCD_TYPE_S6E3HA2_WQHD;
			dsim_info("LCD TYPE : S6E3HA2 (WQHD) : %d type %x\n", dynamic_lcd_type, lcdtype);
		}
	} else {
		dynamic_lcd_type = LCD_TYPE_S6E3HF4_WQHD;
		dsim_info("LCD TYPE : S6E3HF4 (WQHD) : %d\n", dynamic_lcd_type);
	}

exit_panel_ops:
	switch (dynamic_lcd_type) {
	case LCD_TYPE_S6E3HA2_WQHD:
		return &s6e3ha2_panel_ops;
	case LCD_TYPE_S6E3HA3_WQHD:
		return &s6e3ha3_panel_ops;
	case LCD_TYPE_S6E3HF4_WQHD:
		return &s6e3hf4_panel_ops;
	default:
		return &s6e3hf4_panel_ops;
	}
}


